/* Version 0.8.5 Dated Nov 18, 2014 Office*/

body, p, h1, h2, h3, h4, h5, h6 {color: #5A5A5A;}

/*****************************************************************************************/
/***************************************Home**********************************************/
/*****************************************************************************************/
/*
*** Change Logo Size
 */
img.kad-standard-logo{max-width:300px !important;}

/*******************************************************/
/*
*** Navigation + Hover
 */
/* Nav Font Size */
.menu-home.menu-item-30{font-size: 18px;}
/* Nav Hover */
#menu-top-navigation.sf-menu.sf-js-enabled a:hover{border-bottom: 3px solid #EBDDE2;}

/*******************************************************/
/*
*** Book Free Consultation - Homepage
 */
/* Text Align */
div#home-consultation-box{text-align: center;}
/* Consultation Header */
.home-consultation-header{font-size: 28px;}
/* Consultation Description */
.home-consultation-info{font-size: 19px; margin-top:20px;}
/* Consultation Button */
.home-consultation-link-paragraph{margin-top: 40px;}
/* Consultation Button Style*/
a.home-consultation-link:link, a.home-consultation-link:visited{font-size: 21px; color: #5A5A5A; padding: 5px 10px; border: 3px solid #000;}
/* Consultation Button Hover Style*/
a.home-consultation-link:hover{font-size: 21px; color: #FFF; padding: 5px 10px; border: 3px solid #000; background: #5A5A5A}

/*******************************************************/
/*
*** Welcome to Synergy 'Statement'
 */
/* The whole Welcome section */
div#pg-16-1{max-width: 900px; margin: auto;}
/* Welcome Header */
div#pg-16-1>div#panel-16-1-0-0>h3.widget-title{font-weight: 600;}
/* Welcome intro */
div#panel-16-1-0-0 div.textwidget {text-align: justify;}
.welcome-intro {font-size: 19px; line-height: 1.3em;}
/* See Terms Button Style */
a.see_our_team:link,a.see_our_team:visited{font-size: 21px; padding: 5px 10px; border: 3px solid #000; color: #000;}
/* See Terms Button Hover Style */
a.see_our_team:hover{font-size: 21px; padding: 5px 10px; border: 3px solid #000; color: #000; background-color: #EBDDE2;}

/*******************************************************/
/*
*** See Our Monthly Special
 */
/* The Whole Section */
div#pg-16-4>div#pgc-16-4-0>div#panel-16-4-0-0>div.textwidget{width: auto; height: 150px; background-image: url(http://beaucare.ca/wp-content/uploads/2014/10/special-offer-bg.gif); text-align: center;}
/* Monthly Special Font Style */
.monthly-special {font-size: 28px; padding-top: 60px;}
/* Monthly Special Button Style */
a.monthly-special-link:link, a.monthly-special-link:visited { padding: 5px 10px; border: 3px solid #000; color: #000;}
/* Monthly Special Button Hover Style */
a.monthly-special-link:hover { padding: 5px 10px; border: 3px solid #000; color: #000; background-color: #EBDDE2;}
/* Monthly Special Button With Responsive Style*/
@media (max-device-width: 375px) {.monthly-special {font-size: 20px; padding-top: 60px;}}
@media (max-device-width: 285px) {.monthly-special {font-size: 14px;}}

/*******************************************************/
/* 
*** Touch up for miscellaneous combinations
 */
/* Treatment and Skin Condition Header Background */
div#panel-16-2-0-0>h3, div#panel-16-3-0-0>h3{background-color: #EBDDE2; margin-bottom: 0; padding-left: 15px; font-size: 18px;}
/* Top margin of Welcome to Synergy, Treatment and Skin Condition */
div#pg-16-1, div#pg-16-2, div#pg-16-3, div#pg-16-4{margin-top: 80px;}





/**********************************************************************************************/
/**************************************Global Topbar Style*************************************/
/**********************************************************************************************/
/*
*** Change Social Media, Cart, Search to Black
 */
.topbarmenu ul li a, #topbar-search #searchsubmit, #topbar-search input[type=text] {color: #5A5A5A} 

/*******************************************************/
/*
*** Topbar width change
 */
/* Social Media */
section#topbar.topclass .col-md-6.col-sm-6.kad-topbar-left {width: 30%;}
/* Search Bar */
section#topbar.topclass .col-md-6.col-sm-6.kad-topbar-right {width: 70%;}

/*******************************************************/
/*
*** Left Right topbar width
 */
div.col-md-6.col-sm-6.kad-topbar-right{width: 55%;}
div.col-md-6.col-sm-6.kad-topbar-left{width: 45%;}

/*
*** Change Placeholder of Search to Black
 */
#topbar-search .form-search ::-webkit-input-placeholder{color: #5A5A5A;}
#topbar-search .form-search :-moz-placeholder{color: #5A5A5A;}
#topbar-search .form-search ::-moz-placeholder{color: #5A5A5A;}
#topbar-search .form-search :-ms-input-placeholder {color: #5A5A5A;}

/*******************************************************/
/*
*** Search Bar
 */
/* Location Text */
.location-topbar {font-size: 13px; float:right; margin-top: 10px; color: #000;}
/* Search Input */
div#topbar-search.topbar-widget > form#searchform.form-search {margin:2px 0 0 0;}
form#searchform.form-search input#s.search-query, form#searchform.form-search button#searchsubmit.search-icon {height: 30px;}





/**********************************************************************************************/
/************************Skin-condition global style*******************************************/
/**********************************************************************************************/
/*
*** Page header remove
 */
body.single-portfolio #pageheader.titleclass {display: none;}

/*******************************************************/
